document.addEventListener("DOMContentLoaded", function() {

    const navLinks = document.getElementById('navLinks')
    const x_crestik = document.getElementById('X-crestik')
    const poloski_3 = document.getElementById('poloski_3')

    poloski_3.addEventListener('click', function() {
        if(x_crestik.classList.contains('hidden')) {
            x_crestik.classList.remove('hidden')
            x_crestik.classList.add('visible')

            poloski_3.classList.remove('visible')
            poloski_3.classList.add('hidden')

            navLinks.style.display = 'block'
        }
        x_crestik.addEventListener('click', function() {
            if(poloski_3.classList.contains('hidden')) {
                poloski_3.classList.remove('hidden')
                poloski_3.classList.add('visible')

                x_crestik.classList.remove('visible')
                x_crestik.classList.add('hidden')

                navLinks.style.display = 'none'
            }
        })

    })

})